import styled from 'styled-components';
import {Link as LinkRout} from 'react-router-dom';
import {Link as LinkScroll} from 'react-scroll';

export const Nav = styled.nav`
    background: #000;
    height: 80px;
    // margin-top: -80px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-Size: 40px;
    position: sticky;
    top: 0;
    z-Index: 10;

    @media screen and (max-width: 950px){
        transition: 0.8s all ease;
    }
`

export const NavbarContainer = styled.div`
    display: flex;
    justify-Content: space-between;
    height: 80px;
    width: 100%;
    z-index: 1;
    padding: 0 26px;
    max-width: 1100px;
`

export const NavLogo = styled(LinkRout)`
    color: white;
    justify-self: flex-start;
    cursor: pointer;
    font-size: 40px;
    display: flex;
    align-Items: center;
    margin-left: 24px;
    font-weight: 600;
    text-decoration: none;
`
export const NavIcon = styled.div`
    // display: none;

    display: block;
    position: absolute;
    top: 0;
    right: 0;
    transform: translate(-100%, 60%);
    font-Size: 1.8rem;
    cursor: pointer;
    color: #fff;

    // @media screen and (maxWidth: 760px){
    //     display: block;
    //     position: absolute;
    //     top: 0;
    //     right: 0;
    //     transform: translate(-100%, 60%);
    //     font-Size: 1.8rem;
    //     cursor: pointer;
    //     color: #fff;
    // }
`

export const NavigationMenu =styled.ul`
    display: flex;
    align-Items: center;
    list-style: none;
    text-align: center;
    margin-right: -24px;

    @media screen and (max-width: 768px;){
        display: none;
    }
`
export const NavigationItems = styled.li`
    height: 88px;
`

export const NavigationLinks = styled(LinkScroll)`
    color: #fff;
    display: flex;
    align-item: center;
    text-decoration: none;
    padding: 0px;
    height: 100%;
    cursor: pointer;

    &.active {
        border-bottom: 2px solid #03fcdb;
        transition: .3s;
    }
`
