import styled from 'styled-components';
import {MdKeyboardArrowRight,MdArrowForward} from 'react-icons/md';

export const TemaContainer = styled.div`
    background: red;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 950px;
    position: relative;
    z-zIndex: 1;

    // :before{
    //     content: '';
    //     position: absolute;
    //     top: 0;
    //     right: 0;
    //     left: 0;
    //     bottom: 0;
    //     // background: linear-gradient(180deg, rgba(0,0,0,0.1) 0%, rgba(0,0,0,0.3) 40%),
    //     //     Linear-gradient(
    //     //         180deg, 
    //     //         rgba(0,0,0,0.1) 0%,
    //     //         rgba(0,0,0,0.3) 40%
    //     //     )
    //     // ;
    //     z-index: 2;

    // }
`;

export const TeamBG = styled.div`
    position: absolute;
    top: -78px;
    right: 0;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 100%
    overflow: hidden;
`;

export const VdoBG = styled.video`
    height: 100%;
    width: 100%;
    -o-object-fit: cover;
    object-fit: cover;
    background: black;
`;

export const TeamContent = styled.div`
    z-Index: 1;
    max-width: 1200px;
    position: absolute; 
    padding: 8px 24px;
    flex-direction: column;
    align-items: left;

`;

export const TeamH1 = styled.h1`
    color: white;
    font-size: 48px;
    text-align: center;

    @media screen and (max-width: 960px){
        font-size: 40px;
    }

    @media screen and (max-width: 480px){
        font-size: 32px;
    }
`;

export const TeamP = styled.p`
    margin-top: 24px;
    color: white;
    font-size: 24px;
    text-align: left;
    max-width: 600px; 

    @media screen and (max-width: 960px){
        font-size: 18px;
    }

    @media screen and (max-width: 480px){
        font-size: 16px;
    }
`;

export const ButtonWrper = styled.div`
    margin-top: 34px;
    display: flex;
    flex-direction: column;
    align-items: left;
`;

export const ArrowForward = styled(MdArrowForward)`
    margin-left: 8px;
    font-size: 20px;

`

export const ArrowRight = styled(MdKeyboardArrowRight)`
    margin-left: 8px;
    font-size: 20px;
`
