import React, { Component } from 'react'

import {
    Container,
    Formwrap,
    FormContent,
    Form,
    FormH1,
    FormLabel,
    FormInput,
    FormButton,
    Text
} from './SignInElements'

class SignIn extends Component{

    // componentDidMount() {
