import React from 'react'
import './Slider.css'

const Content = [{
    title: 'O2 Value',
    description: 'abc',
    color: '#0088FE'
},{
    title: 'turbidity Value',
    description: 'def',
    color: '#00C49F'
},{
    title: 'pH Value',
    description: 'ghi',
    color: '#FFBB28'
}];
const delay = 3000;

const TeamSlider = () => {

    const [index, setIndex] = React.useState(0);

    React.useEffect(() => {
        setTimeout(() => 
            setIndex((prevIndex) => 
                prevIndex === Content.length - 1 ? 0 : prevIndex + 1
            ),
            delay
        );
        return () => {};
    },[index]);

    return (
        
        <div className='slideshow'>
            <div className='slideshowsliders' style={{transform:`translate3d(${-index*100}%, 0, 0)`}}>
                {Content.map((block,index) => (
                    <div className='slide' key={index} style={{block}}>
                        <h1>{block.title}</h1>
                        <p>{block.description}</p>
                    </div>
                ))}
            </div>
            <div className='slideshowdots'>
                {Content.map((_,idx) => (
                    <div key={idx} className='slideshowdot'></div>
                ))}
            </div>
        </div>
        
        // https://tinloof.com/blog/how-to-build-an-auto-play-slideshow-with-react/
        //https://codesandbox.io/s/p582xl40j?file=/index.js 
    )
}

export default TeamSlider
