import React from 'react';
// import { Button } from 'react-scroll';
import Video from '../../Videos/TeamVB.mp4';
import './Slider.css'
// import {Button} from '../ButtonElement';
import{
    TemaContainer,
    VdoBG,
    TeamBG,
    TeamContent,
    TeamH1,
    TeamP,
    // ButtonWrper,
    // ArrowForward,
    // ArrowRight
} from './TeamElements'

const Content = [{
    title: 'O2 Value',
    description: 'abc',
    color: '#0088FE'
},{
    title: 'turbidity Value',
    description: 'def',
    color: '#00C49F'
},{
    title: 'pH Value',
    description: 'ghi',
    color: '#FFBB28'
}];
const delay = 3000;

const Team = () => {

    const [index, setIndex] = React.useState(0);
    const timeOut = React.useRef(null);

    function resetTimeOut(){
        if(timeOut.current){
            clearTimeout(timeOut.current)
        }
    }

    React.useEffect(() => {
        resetTimeOut();
        timeOut.current = setTimeout(() => 
            setIndex((prevIndex) => 
                prevIndex === Content.length - 1 ? 0 : prevIndex + 1
            ),
            delay
        );
        return () => {resetTimeOut();};
        
    },[index]);

    // const [hover, setHover] = useState(false)

    // const onHover = () => {
    //     setHover(!hover)
    // }

    return (
        <>
            <TemaContainer>
                <TeamBG>
                    <VdoBG autoPlay loop muted src={Video} type='video/mp4'/>
                </TeamBG>
                {/* <TeamContent>
                    <TeamH1>Team Visible</TeamH1>
                    <TeamP>
                        AutoPlay slider
                    </TeamP>
                    {/* <ButtonWrper>
                        <Button 
                            to="/signin" 
                            onMouseEnter={onHover} 
                            onMouseLeave={onHover} 
                            primary='true'  
                            dark='true'>
                            Sign In {hover ? <ArrowForward/> : <ArrowRight/>}
                        </Button>
                    </ButtonWrper> */}
                {/* </TeamContent> */}

                <div className='slideshow'>
                    <div className='slideshowsliders' style={{transform:`translate3d(${-index*100}%, 0, 0)`}}>
                        {Content.map((block,index) => (
                            <div className='slide' key={index} style={{block}}>
                                <h1>{block.title}</h1>
                                <p>{block.description}</p>
                            </div>
                        ))}
                    </div>
                    <div className='slideshowdots'>
                        {Content.map((_,idx) => (
                            <div key={idx} 
                            className={`slideshowdot ${index === idx ? "active":""}`}
                            onClick ={() => {
                                setIndex(idx);
                            }}
                            ></div>
                        ))}
                    </div>
                </div>
            </TemaContainer>  
        </>
    )
}

export default Team
