import React from 'react';
// import { Button } from 'react-scroll';
import Video from '../../Videos/TeamVB.mp4';
import './Slider.css'
// import {Button} from '../ButtonElement';
import{
    TemaContainer,
    VdoBG,
    TeamBG,
    TeamContent,
    TeamP,
} from './TeamElements'

const Content = [{
    title: 'Team Visible',
    description1: 'Our main goal is to increase awareness for drinkable',
    description2: 'water and make a halt for kidney diseases caused ',
    description3: 'by consumption of contaminated water drastically,',
    description4: 'which will create a generation free from kidney disorders in Sri Lanka.'
    
},{
    title: 'turbidity Value',
    description: 'def',

},{
    title: 'pH Value',
    description: 'ghi',
    
}];
const delay = 3000;

const Team = () => {

    const [index, setIndex] = React.useState(0);
    const timeOut = React.useRef(null);

    function resetTimeOut(){
        if(timeOut.current){
            clearTimeout(timeOut.current)
        }
    }

    React.useEffect(() => {
        resetTimeOut();
        timeOut.current = setTimeout(() => 
            setIndex((prevIndex) => 
                prevIndex === Content.length - 1 ? 0 : prevIndex + 1
            ),
            delay
        );
        return () => {resetTimeOut();};
        
    },[index]);


    return (
        <>
            <TemaContainer id='about'>
                <TeamBG>
                    <VdoBG autoPlay loop muted src={Video} type='video/mp4'/>
                </TeamBG>
                <TeamContent>
                    <TeamP>
                        <div className='slideshow'>
                            <div className='slideshowsliders' style={{transform:`translate3d(${-index*100}%, 0, 0)`}}>
                                {Content.map((block,index) => (
                                    <div className='slide' key={index} style={{block}}>
                                        <h1>{block.title}</h1>
                                        <p>{block.description1}</p>
                                        <p>{block.description2}</p>
                                        <p>{block.description3}</p>
                                        <p>{block.description4}</p>
                                    </div>
                                ))}
                            </div>
                            <div className='slideshowdots'>
                                {Content.map((_,idx) => (
                                    <div key={idx} 
                                    className={`slideshowdot ${index === idx ? "active":""}`}
                                    onClick ={() => {
                                        setIndex(idx);
                                    }}
                                    ></div>
                                ))}
                            </div>
                        </div>
                    </TeamP>                
                </TeamContent>     
            </TemaContainer>  
        </>
    )
}

export default Team
